import React from "react";
import Articles from "../components/articles";
import Layout from "../components/layout";
import Seo from "../components/seo";
import { fetchAPI } from "../lib/api";

const Home = ({ articles, categories, homepage, global }) => {
  return (
    <Layout categories={categories} global={global}>
      <Seo seo={homepage.seo} />
      <section className="row">
        <div className={`section group`}>
          <div className="col span_7_of_12">Column One</div>
          <div className="col span_5_of_12">Column Two</div>
        </div>
      </section>
      {/* <div className="uk-section">
        <div className="uk-container uk-container-large">
          <h1>{homepage.hero.title}</h1>
          <Articles articles={articles} />
        </div>
      </div> */}
    </Layout>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [articles, categories, homepage] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
    fetchAPI("/homepage"),
  ]);

  return {
    props: { articles, categories, homepage, global },
    revalidate: 1,
  };
}

export default Home;
